import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../utils/api';

const DashboardPage = () => {
  const [organisations, setOrganisations] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [selectedOrg, setSelectedOrg] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrganisations = async () => {
      try {
        const { data } = await API.get('/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setOrganisations(data.organisations || []);
      } catch (err) {
        console.error(err);
      }
    };

    fetchOrganisations();
  }, []);

  const handleSearch = async () => {
    try {
      const { data } = await API.get(`/organisations/search?query=${searchQuery}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setSearchResults(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleRequestAccess = async (orgId) => {
    try {
      await API.post(
        '/users/request-access',
        { orgId, userId: JSON.parse(localStorage.getItem('user'))._id },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      alert('Access request sent');
    } catch (err) {
      console.error(err);
    }
  };

  const handleSelectOrganisation = () => {
    if (selectedOrg) {
      navigate(`/organisation/${selectedOrg}`);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Dashboard</h1>
      {organisations.length > 0 ? (
        <div className="mb-6">
          <label className="block mb-2 text-lg">Select an Organisation:</label>
          <select
            className="w-full p-3 border rounded"
            value={selectedOrg}
            onChange={(e) => setSelectedOrg(e.target.value)}
          >
            <option value="">-- Select Organisation --</option>
            {organisations.map((org) => (
              <option key={org.orgId} value={org.orgId}>
                {org.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleSelectOrganisation}
            className="mt-4 bg-blue-600 text-white p-3 rounded w-full"
          >
            Go to Organisation
          </button>
        </div>
      ) : (
        <div>
          <h2 className="text-2xl font-semibold mb-4">Search for Organisations</h2>
          <input
            type="text"
            placeholder="Search for organisations..."
            className="w-full p-3 border rounded mb-4"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button onClick={handleSearch} className="bg-blue-600 text-white p-3 rounded w-full mb-4">
            Search
          </button>
          <ul>
            {searchResults.map((org) => (
              <li key={org._id} className="mb-2 p-4 border rounded shadow">
                <div className="flex justify-between">
                  <span>{org.name}</span>
                  <button
                    onClick={() => handleRequestAccess(org._id)}
                    className="bg-green-600 text-white px-4 py-2 rounded"
                  >
                    Request Access
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
      <button
        onClick={() => navigate('/create-organisation')}
        className="mt-6 bg-gray-800 text-white p-3 rounded w-full"
      >
        Create Organisation
      </button>
    </div>
  );
};

export default DashboardPage;
