import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../utils/api'; // Ensure your API utility file path is correct

const OrganisationPage = () => {
  const { orgId } = useParams();
  const [organisation, setOrganisation] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [activeTab, setActiveTab] = useState('details');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrganisationDetails = async () => {
      try {
        const { data } = await API.get(`/organisations/${orgId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setOrganisation(data);

        // Check if the logged-in user is an admin
        const currentUserId = JSON.parse(localStorage.getItem('user'))._id;
        const adminMember = data.members.find(
          (member) => member.userId._id === currentUserId && member.role === 'Admin'
        );
        setIsAdmin(!!adminMember);
      } catch (err) {
        console.error('Failed to fetch organisation details:', err);
      }
    };

    fetchOrganisationDetails();
  }, [orgId]);

  const handleAccessRequestUpdate = async (userId, status, role = null) => {
    try {
      await API.post(
        '/organisations/access-request',
        { orgId, userId, status, role },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );

      // Update the state to reflect the changes
      setOrganisation((prevOrganisation) => ({
        ...prevOrganisation,
        accessRequests: prevOrganisation.accessRequests.filter(
          (request) => request.userId._id !== userId
        ),
      }));

      alert(`Access request has been ${status.toLowerCase()}`);
    } catch (err) {
      console.error('Failed to update access request:', err);
      alert('An error occurred while processing the request.');
    }
  };

  const handleEditOrganisation = () => {
    navigate(`/organisation/${orgId}/edit`);
  };

  const handleDeleteOrganisation = async () => {
    if (window.confirm('Are you sure you want to delete this organisation?')) {
      try {
        await API.post(
          '/organisations/delete',
          { orgId },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          }
        );
        alert('Organisation deleted successfully');
        navigate('/dashboard');
      } catch (err) {
        console.error('Failed to delete organisation:', err);
      }
    }
  };

  const handleManageMembers = () => {
    navigate(`/organisation/${orgId}/members`);
  };

  const handleAccessRequests = () => {
    setActiveTab('access-requests');
  };

  const handleViewBilling = () => {
    navigate(`/organisation/${orgId}/billing`);
  };

  const renderDetailsTab = () => (
    <div>
      <h1 className="text-3xl font-bold mb-4">{organisation.name}</h1>
      <p className="mb-4">{organisation.description}</p>
      <p className="mb-4">
        <strong>Address:</strong> {organisation.address}
      </p>
      <p className="mb-4">
        <strong>Contact Email:</strong> {organisation.contactEmail}
      </p>
      <p className="mb-4">
        <strong>Phone Number:</strong> {organisation.phoneNumber}
      </p>
      <p className="mb-4">
        <strong>Website:</strong>{' '}
        <a href={organisation.website} target="_blank" rel="noopener noreferrer">
          {organisation.website}
        </a>
      </p>
      <p className="mb-4">
        <strong>Incorporation ID:</strong> {organisation.incorporationId}
      </p>
    </div>
  );

  const renderAdminTab = () => (
    <div>
      <h2 className="text-2xl font-bold mb-4">Admin Actions</h2>
      <button
        onClick={handleEditOrganisation}
        className="bg-blue-600 text-white p-3 rounded mr-4 mb-4"
      >
        Edit Organisation
      </button>
      <button
        onClick={handleManageMembers}
        className="bg-green-600 text-white p-3 rounded mr-4 mb-4"
      >
        Manage Members
      </button>
      <button
        onClick={handleAccessRequests}
        className="bg-yellow-600 text-white p-3 rounded mr-4 mb-4"
      >
        View Access Requests
      </button>
      <button
        onClick={handleViewBilling}
        className="bg-purple-600 text-white p-3 rounded mr-4 mb-4"
      >
        View Billing Details
      </button>
      <button
        onClick={handleDeleteOrganisation}
        className="bg-red-600 text-white p-3 rounded mb-4"
      >
        Delete Organisation
      </button>
    </div>
  );

  const renderAccessRequests = () => (
    <div>
      <h2 className="text-2xl font-bold mb-4">Access Requests</h2>
      {organisation.accessRequests && organisation.accessRequests.length > 0 ? (
        organisation.accessRequests.map((request) => (
          <div key={request._id} className="mb-4 p-4 border rounded">
            <p>
              <strong>User:</strong> {request.userId.firstName} {request.userId.lastName}
            </p>
            <p>
              <strong>Email:</strong> {request.userId.email}
            </p>
            <p>
              <strong>Status:</strong> {request.status}
            </p>
            <button
              onClick={() =>
                handleAccessRequestUpdate(request.userId._id, 'Approved', 'Member')
              }
              className="bg-green-600 text-white p-2 rounded mr-4"
            >
              Approve
            </button>
            <button
              onClick={() => handleAccessRequestUpdate(request.userId._id, 'Rejected')}
              className="bg-red-600 text-white p-2 rounded"
            >
              Reject
            </button>
          </div>
        ))
      ) : (
        <p>No pending access requests.</p>
      )}
    </div>
  );

  if (!organisation) return <div>Loading organisation details...</div>;

  return (
    <div className="p-6">
      <div className="tabs">
        <button
          onClick={() => setActiveTab('details')}
          className={`tab ${activeTab === 'details' ? 'active' : ''}`}
        >
          Organisation Details
        </button>
        {isAdmin && (
          <button
            onClick={() => setActiveTab('admin')}
            className={`tab ${activeTab === 'admin' ? 'active' : ''}`}
          >
            Admin Panel
          </button>
        )}
        {isAdmin && (
          <button
            onClick={() => setActiveTab('access-requests')}
            className={`tab ${activeTab === 'access-requests' ? 'active' : ''}`}
          >
            Access Requests
          </button>
        )}
      </div>

      <div className="tab-content">
        {activeTab === 'details' && renderDetailsTab()}
        {activeTab === 'admin' && renderAdminTab()}
        {activeTab === 'access-requests' && renderAccessRequests()}
      </div>
    </div>
  );
};

export default OrganisationPage;
