import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../utils/api';

const OrganisationPage = () => {
  const { orgId } = useParams();
  const [organisation, setOrganisation] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrganisation = async () => {
      try {
        const { data } = await API.get(`/organisations/${orgId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setOrganisation(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchOrganisation();
  }, [orgId]);

  const handleEdit = () => {
    navigate(`/organisation/${orgId}/edit`);
  };

  const handleDelete = async () => {
    try {
      await API.post('/organisations/delete', { orgId }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      alert('Organisation deleted');
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
    }
  };

  if (!organisation) return <div>Loading...</div>;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold">{organisation.name}</h1>
      <p>{organisation.description}</p>
      <div className="mt-4">
        {organisation.members.some((member) => member.userId._id === JSON.parse(localStorage.getItem('user'))._id && member.role === 'Admin') && (
          <>
            <button onClick={handleEdit} className="bg-blue-600 text-white p-3 rounded mr-4">
              Edit Organisation
            </button>
            <button onClick={handleDelete} className="bg-red-600 text-white p-3 rounded">
              Delete Organisation
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default OrganisationPage;
