import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const componentMapping = {
  budget: 'Budget Management',
  taskManagement: 'Task Management',
  documentation: 'Documentation Management',
};

const ManageMembersAndComponents = () => {
  const { orgId } = useParams();
  const [members, setMembers] = useState([]);
  const [enabledComponents, setEnabledComponents] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await API.get(`/organisations/${orgId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setMembers(data.members || []);
        setEnabledComponents(data.servicesEnabled || {});
      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, [orgId]);

  const handleRoleChange = async (userId, newRole) => {
    setLoading(true);
    try {
      await API.put(
        '/organisations/edit-user-access',
        { orgId, userId, updates: { role: newRole } },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setMembers((prev) =>
        prev.map((member) =>
          member.userId._id === userId ? { ...member, role: newRole } : member
        )
      );
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleComponentToggle = async (userId, component) => {
    setLoading(true);
    try {
      await API.put(
        '/organisations/update-components',
        {
          orgId,
          userId,
          component,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setMembers((prev) =>
        prev.map((member) =>
          member.userId._id === userId
            ? {
                ...member,
                servicesAccess: {
                  ...member.servicesAccess,
                  [component]: !member.servicesAccess[component],
                },
              }
            : member
        )
      );
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveMember = async (userId) => {
    try {
      await API.post(
        '/organisations/remove-member',
        { orgId, userId },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setMembers((prev) => prev.filter((member) => member.userId._id !== userId));
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <div className="text-center">Loading...</div>;

  return (
    <div className="p-6 bg-gradient-to-br from-gray-100 to-gray-200 min-h-screen">
      <h1 className="text-2xl font-bold text-blue-600 mb-6">Manage Members & Components</h1>

      <div className="overflow-auto bg-white shadow-md rounded-lg">
        <table className="min-w-full border-collapse border border-gray-300 text-sm text-gray-800">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-300 p-2 text-left">Name</th>
              <th className="border border-gray-300 p-2 text-left">Role</th>
              {Object.keys(componentMapping).map((component) => (
                <th key={component} className="border border-gray-300 p-2 text-left">
                  {componentMapping[component]}
                </th>
              ))}
              <th className="border border-gray-300 p-2 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {members.map((member) => (
              <tr key={member.userId._id} className="hover:bg-gray-50">
                <td className="border border-gray-300 p-2">
                  {member.userId.firstName} {member.userId.lastName}
                </td>
                <td className="border border-gray-300 p-2">
                  <select
                    className="p-1 border border-gray-300 rounded"
                    value={member.role}
                    onChange={(e) => handleRoleChange(member.userId._id, e.target.value)}
                  >
                    <option value="Admin">Admin</option>
                    <option value="Member">Member</option>
                  </select>
                </td>
                {Object.keys(componentMapping).map((component) => (
                  <td key={component} className="border border-gray-300 p-2 text-center">
                    <input
                      type="checkbox"
                      checked={!!member.servicesAccess[component]}
                      onChange={() => handleComponentToggle(member.userId._id, component)}
                    />
                  </td>
                ))}
                <td className="border border-gray-300 p-2">
                  <button
                    onClick={() => handleRemoveMember(member.userId._id)}
                    className="text-red-600 hover:underline"
                  >
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageMembersAndComponents;
