import React, { useState, useEffect } from "react";
import WorkflowDesigner from "./WorkflowDesigner";
import API from "../utils/api";

const BudgetWorkflowDesigner = ({ orgId }) => {
  const [workflows, setWorkflows] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Fetch existing workflows for the organisation
    const fetchWorkflows = async () => {
      try {
        setLoading(true);
        const { data } = await API.get(`/workflows/${orgId}/budget`);
        setWorkflows(data);
      } catch (error) {
        console.error("Error fetching workflows:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkflows();
  }, [orgId]);

  const handleSaveWorkflow = async (newWorkflow) => {
    try {
      const { data } = await API.post(`/workflows/${orgId}/budget`, { workflow: newWorkflow });
      setWorkflows((prev) => [...prev, data]);
      alert("Workflow saved successfully!");
    } catch (error) {
      console.error("Error saving workflow:", error);
      alert("Failed to save workflow.");
    }
  };

  return (
    <div className="p-6 bg-gradient-to-br from-gray-100 to-gray-200 min-h-screen">
      <h1 className="text-3xl font-semibold text-gray-800 mb-6">
        Budget Workflow Designer
      </h1>
      {loading ? (
        <div className="text-center">Loading workflows...</div>
      ) : (
        <WorkflowDesigner
          workflows={workflows}
          onSaveWorkflow={handleSaveWorkflow}
          component="Budget"
        />
      )}
    </div>
  );
};

export default BudgetWorkflowDesigner;
