import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../utils/api';

const OrganisationPage = () => {
  const { orgId } = useParams();
  const [organisation, setOrganisation] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [activeTab, setActiveTab] = useState('details');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrganisationDetails = async () => {
      try {
        const { data } = await API.get(`/organisations/${orgId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setOrganisation(data);

        // Check if the logged-in user is an admin
        const currentUserId = JSON.parse(localStorage.getItem('user'))._id;
        const adminMember = data.members.find(
          (member) => member.userId._id === currentUserId && member.role === 'Admin'
        );
        setIsAdmin(!!adminMember);
      } catch (err) {
        console.error('Failed to fetch organisation details:', err);
      }
    };

    fetchOrganisationDetails();
  }, [orgId]);

  const handleDeleteOrganisation = async () => {
    if (window.confirm('Are you sure you want to delete this organisation?')) {
      try {
        await API.post(
          '/organisations/delete',
          { orgId },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          }
        );
        alert('Organisation deleted successfully');
        navigate('/dashboard');
      } catch (err) {
        console.error('Failed to delete organisation:', err);
        alert('Error occurred while deleting the organisation.');
      }
    }
  };

  const handleAccessRequestUpdate = async (userId, status, role = null) => {
    try {
      await API.post(
        '/organisations/access-request',
        { orgId, userId, status, role },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );

      setOrganisation((prevOrganisation) => ({
        ...prevOrganisation,
        accessRequests: prevOrganisation.accessRequests.filter(
          (request) => request.userId._id !== userId
        ),
      }));

      alert(`Access request has been ${status.toLowerCase()}`);
    } catch (err) {
      console.error('Failed to update access request:', err);
      alert('Error occurred while updating access request.');
    }
  };

  const renderDetailsTab = () => (
    <div className="rounded-lg shadow p-8 bg-white">
      <h1 className="text-3xl font-semibold text-gray-800 mb-6">{organisation.name}</h1>
      <p className="text-gray-700 mb-4">
        <strong>Description:</strong> {organisation.description}
      </p>
      <p className="text-gray-700 mb-4">
        <strong>Address:</strong> {organisation.address}
      </p>
      <p className="text-gray-700 mb-4">
        <strong>Contact Email:</strong> {organisation.contactEmail}
      </p>
      <p className="text-gray-700 mb-4">
        <strong>Phone Number:</strong> {organisation.phoneNumber}
      </p>
      <p className="text-gray-700 mb-4">
        <strong>Website:</strong>{' '}
        <a
          href={organisation.website}
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-600 hover:underline"
        >
          {organisation.website}
        </a>
      </p>
      <p className="text-gray-700 mb-4">
        <strong>Incorporation ID:</strong> {organisation.incorporationId}
      </p>
    </div>
  );

  const renderAdminTab = () => (
    <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3 bg-gray-50 p-6 rounded-lg shadow">
      <button
        onClick={() => navigate(`/organisation/${orgId}/edit`)}
        className="bg-gray-800 text-white py-3 px-6 rounded-lg shadow hover:bg-gray-900 transition"
      >
        Edit Organisation
      </button>
      <button
        onClick={() => navigate(`/organisation/${orgId}/members`)}
        className="bg-green-700 text-white py-3 px-6 rounded-lg shadow hover:bg-green-800 transition"
      >
        Manage Members
      </button>
      <button
        onClick={() => setActiveTab('access-requests')}
        className="bg-yellow-600 text-white py-3 px-6 rounded-lg shadow hover:bg-yellow-700 transition"
      >
        View Access Requests
      </button>
      <button
        onClick={() => navigate(`/organisation/${orgId}/billing`)}
        className="bg-blue-700 text-white py-3 px-6 rounded-lg shadow hover:bg-blue-800 transition"
      >
        View Billing Details
      </button>
      <button
        onClick={handleDeleteOrganisation}
        className="bg-red-600 text-white py-3 px-6 rounded-lg shadow hover:bg-red-700 transition"
      >
        Delete Organisation
      </button>
    </div>
  );

  const renderAccessRequests = () => (
    <div className="rounded-lg shadow p-8 bg-white">
      <h2 className="text-2xl font-semibold text-gray-800 mb-6">Access Requests</h2>
      {organisation.accessRequests && organisation.accessRequests.length > 0 ? (
        organisation.accessRequests.map((request) => (
          <div key={request._id} className="mb-6 p-4 border rounded shadow">
            <p className="text-gray-700 mb-2">
              <strong>Name:</strong> {request.userId.firstName} {request.userId.lastName}
            </p>
            <p className="text-gray-700 mb-2">
              <strong>Email:</strong> {request.userId.email}
            </p>
            <div className="flex gap-4">
              <button
                onClick={() =>
                  handleAccessRequestUpdate(request.userId._id, 'Approved', 'Member')
                }
                className="bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition"
              >
                Approve
              </button>
              <button
                onClick={() => handleAccessRequestUpdate(request.userId._id, 'Rejected')}
                className="bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700 transition"
              >
                Reject
              </button>
            </div>
          </div>
        ))
      ) : (
        <p className="text-gray-600">No pending access requests.</p>
      )}
    </div>
  );

  if (!organisation) return <div className="text-center text-gray-600">Loading...</div>;

  return (
    <div className="p-6">
      <div className="flex gap-4 mb-8">
        <button
          onClick={() => setActiveTab('details')}
          className={`py-2 px-4 rounded ${
            activeTab === 'details' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-800'
          }`}
        >
          Organisation Details
        </button>
        {isAdmin && (
          <>
            <button
              onClick={() => setActiveTab('admin')}
              className={`py-2 px-4 rounded ${
                activeTab === 'admin' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-800'
              }`}
            >
              Admin Panel
            </button>
            <button
              onClick={() => setActiveTab('access-requests')}
              className={`py-2 px-4 rounded ${
                activeTab === 'access-requests'
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-100 text-gray-800'
              }`}
            >
              Access Requests
            </button>
          </>
        )}
      </div>

      {activeTab === 'details' && renderDetailsTab()}
      {activeTab === 'admin' && renderAdminTab()}

    </div>
  );
};

export default OrganisationPage;
