import React, { useState, useEffect } from "react";
import API from "../utils/api";

const RevenuePage = ({ orgId }) => {
  const [revenues, setRevenues] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filters, setFilters] = useState({
    category: "",
    subcategory: "",
    startDate: "",
    endDate: "",
  });
  const [analytics, setAnalytics] = useState(null);

  // Fetch revenues
  useEffect(() => {
    const fetchRevenues = async () => {
      try {
        const { data } = await API.get("/revenues", {
          params: { organisationId: orgId, ...filters },
        });
        setRevenues(data);
      } catch (error) {
        console.error("Error fetching revenues:", error);
      }
    };

    fetchRevenues();
  }, [filters, orgId]);

  // Fetch analytics
  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        const { data } = await API.get("/revenues/analytics", {
          params: { organisationId: orgId },
        });
        setAnalytics(data);
      } catch (error) {
        console.error("Error fetching analytics:", error);
      }
    };

    fetchAnalytics();
  }, [orgId]);

  // Handle form submissions
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddRevenue = async (revenue) => {
    try {
      const { data } = await API.post("/revenues/create", {
        ...revenue,
        organisationId: orgId,
      });
      setRevenues((prev) => [...prev, data.revenue]);
    } catch (error) {
      console.error("Error adding revenue:", error);
    }
  };

  const handleUpdateRevenue = async (revenueId, updates) => {
    try {
      const { data } = await API.put(`/revenues/${revenueId}`, updates);
      setRevenues((prev) =>
        prev.map((rev) => (rev._id === revenueId ? data.revenue : rev))
      );
    } catch (error) {
      console.error("Error updating revenue:", error);
    }
  };

  const handleDeleteRevenue = async (revenueId) => {
    try {
      await API.delete(`/revenues/${revenueId}`);
      setRevenues((prev) => prev.filter((rev) => rev._id !== revenueId));
    } catch (error) {
      console.error("Error deleting revenue:", error);
    }
  };

  return (
    <div className="p-6 bg-gradient-to-br from-gray-100 to-gray-200 min-h-screen">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">Revenue Management</h2>

      {/* Add Revenue Form */}
      <div className="mb-6 p-4 bg-white shadow rounded">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Add Revenue</h3>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const formData = new FormData(e.target);
            const revenue = Object.fromEntries(formData.entries());
            handleAddRevenue(revenue);
            e.target.reset();
          }}
        >
          <div className="grid grid-cols-2 gap-4">
            <input
              type="number"
              name="amount"
              placeholder="Amount"
              className="p-2 border rounded"
              required
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              className="p-2 border rounded"
              required
            />
            <input
              type="text"
              name="subcategory"
              placeholder="Subcategory"
              className="p-2 border rounded"
            />
            <input
              type="date"
              name="date"
              className="p-2 border rounded"
              required
            />
            <input
              type="text"
              name="source"
              placeholder="Source"
              className="p-2 border rounded"
            />
            <button
              type="submit"
              className="col-span-2 bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
            >
              Add Revenue
            </button>
          </div>
        </form>
      </div>

      {/* Filter Revenues */}
      <div className="mb-6 p-4 bg-white shadow rounded">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Filter Revenues</h3>
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="category"
            placeholder="Category"
            className="p-2 border rounded"
            onChange={handleFilterChange}
          />
          <input
            type="text"
            name="subcategory"
            placeholder="Subcategory"
            className="p-2 border rounded"
            onChange={handleFilterChange}
          />
          <input
            type="date"
            name="startDate"
            className="p-2 border rounded"
            onChange={handleFilterChange}
          />
          <input
            type="date"
            name="endDate"
            className="p-2 border rounded"
            onChange={handleFilterChange}
          />
        </div>
      </div>

      {/* Revenue List */}
      <div className="mb-6 p-4 bg-white shadow rounded">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Revenue List</h3>
        <ul>
          {revenues.map((rev) => (
            <li key={rev._id} className="mb-2 flex justify-between items-center">
              <div>
                <p className="font-semibold">{rev.category} - {rev.subcategory}</p>
                <p className="text-gray-600">{rev.amount} on {new Date(rev.date).toLocaleDateString()}</p>
              </div>
              <div className="flex gap-2">
                <button
                  className="bg-yellow-500 text-white py-1 px-2 rounded hover:bg-yellow-600"
                  onClick={() =>
                    handleUpdateRevenue(rev._id, { amount: rev.amount + 10 }) // Example update
                  }
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white py-1 px-2 rounded hover:bg-red-600"
                  onClick={() => handleDeleteRevenue(rev._id)}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* Analytics */}
      {analytics && (
        <div className="mb-6 p-4 bg-white shadow rounded">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">Revenue Analytics</h3>
          <p><strong>Total Revenues:</strong> {analytics.totalRevenues}</p>
          <p><strong>Total Received:</strong> {analytics.totalReceived}</p>
          <p><strong>Outstanding:</strong> {analytics.outstanding}</p>
        </div>
      )}
    </div>
  );
};

export default RevenuePage;
