import React, { useState } from "react";

const WorkflowDesigner = ({ workflows, onSaveWorkflow, component }) => {
  const [newWorkflow, setNewWorkflow] = useState([]);

  const handleAddStep = () => {
    setNewWorkflow((prev) => [...prev, { step: `Step ${prev.length + 1}`, actions: [] }]);
  };

  const handleSave = () => {
    onSaveWorkflow(newWorkflow);
    setNewWorkflow([]);
  };

  return (
    <div className="bg-white shadow-lg rounded p-6">
      <h2 className="text-xl font-semibold mb-4">{component} Workflow Designer</h2>
      <div className="mb-4">
        {newWorkflow.map((step, index) => (
          <div key={index} className="mb-2 p-4 border rounded">
            <h3 className="font-bold">Step {index + 1}</h3>
            {/* Add drag-and-drop areas or actions */}
          </div>
        ))}
      </div>
      <div className="flex gap-4">
        <button
          onClick={handleAddStep}
          className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Add Step
        </button>
        <button
          onClick={handleSave}
          className="bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700"
        >
          Save Workflow
        </button>
      </div>
    </div>
  );
};

export default WorkflowDesigner;
