import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../utils/api';

const CreateOrganisationPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    incorporationId: '',
    description: '',
    address: '',
    phoneNumber: '',
    contactEmail: '',
    website: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleCreateOrganisation = async (e) => {
    e.preventDefault();
    try {
      await API.post('/organisations/create', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create organisation');
    }
  };

  return (
    <div className="flex h-screen items-center justify-center bg-gray-50">
      <form
        onSubmit={handleCreateOrganisation}
        className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg"
      >
        <h1 className="text-2xl font-bold mb-4">Create Organisation</h1>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <input
          type="text"
          placeholder="Organisation Name"
          className="w-full p-3 mb-4 border rounded"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Incorporation ID"
          className="w-full p-3 mb-4 border rounded"
          value={formData.incorporationId}
          onChange={(e) => setFormData({ ...formData, incorporationId: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          className="w-full p-3 mb-4 border rounded"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Address"
          className="w-full p-3 mb-4 border rounded"
          value={formData.address}
          onChange={(e) => setFormData({ ...formData, address: e.target.value })}
        />
        <input
          type="text"
          placeholder="Phone Number"
          className="w-full p-3 mb-4 border rounded"
          value={formData.phoneNumber}
          onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
        />
        <input
          type="email"
          placeholder="Contact Email"
          className="w-full p-3 mb-4 border rounded"
          value={formData.contactEmail}
          onChange={(e) => setFormData({ ...formData, contactEmail: e.target.value })}
        />
        <input
          type="text"
          placeholder="Website"
          className="w-full p-3 mb-4 border rounded"
          value={formData.website}
          onChange={(e) => setFormData({ ...formData, website: e.target.value })}
        />
        <button type="submit" className="w-full bg-blue-600 text-white p-3 rounded">
          Create Organisation
        </button>
      </form>
    </div>
  );
};

export default CreateOrganisationPage;
